<script>
    $(document).ready(function() {
            $(document).on('click', '.pagination a', function(e) {
                e.preventDefault();
                let page = $(this).attr('href').split('page=')[1];
                notification(page);
            });

            function notification(page) {
                $.ajax({
                    url: "/pagination/paginate-data?page=" + page,
                    success: function(res) {
                        $('.notifications').html(res);
                    },
                    error: function(xhr, status, error) {
                        console.log(error);
                        // You can add further error handling logic here, like showing an error message to the user.
                    }
                });
            }
        });
        $(document).ready(function() {
            $(document).on('click', '.orders pagination a', function(e) {
                e.preventDefault();
                let page = $(this).attr('href').split('page=')[1];
                orders(page);
            });

            function orders(page) {
                $.ajax({
                    url: "/pagination-orders/paginate-data?page=" + page,
                    success: function(res) {
                        $('.orders').html(res);
                    },
                    error: function(xhr, status, error) {
                        console.log(error);
                        // You can add further error handling logic here, like showing an error message to the user.
                    }
                });
            }
        });
        $(document).on('click', '.delete-address', function(e) {
            e.preventDefault();

            const addressId = $(this).data('id');
            const deleteUrl = "{{ route('address.destroy', ':id') }}".replace(':id', addressId);

            $.ajax({
                type: 'DELETE',
                url: deleteUrl,
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                },

                success: function(res) {

                    toasterSuccess("{{ __('web.address_deleted_successfully') }}");
                    setTimeout(function() {
                        location.reload();
                    }, 1000);


                },
                error: function(xhr, status, error) {
                    console.log(xhr.responseText);
                    toastr.error('Failed to delete address');
                }
            });
        });
</script>
